        highFrequency: 2000
      }
    ]
  },
  'Electric': {
    type: 'synth',
    settings: {
      oscillator: { 
        type: "sawtooth",
        modulationType: "square",
        modulationIndex: 0.3,
        harmonicity: 0.5
      },
      envelope: {
        attack: 0.1,
        decay: 0.3,
        sustain: 0.3,
        release: 1
      },
      filter: {
        type: "lowpass",
        frequency: 600,
        rolloff: -24,
        Q: 1
      },
      filterEnvelope: {
        attack: 0.2,
        decay: 0.3,
        sustain: 0.4,
        release: 1,
        baseFrequency: 200,
        octaves: 1.5
      },
      volume: -10
    },
    effects: [
      {
        type: "compressor",
        threshold: -20,
        ratio: 6,
        attack: 0.1,
        release: 0.2
      },
      {
        type: "eq3",
        low: 4,
        mid: -2,
        high: -6,
        lowFrequency: 150,
        highFrequency: 1500
      }
    ]
  },
  'Synth': {
    type: 'synth',
    settings: {
      oscillator: { 
        type: "triangle",
        modulationType: "sine",
        modulationIndex: 0.4,
        harmonicity: 0.5
      },
